import { Component } from '@angular/core';
import { Mascota } from 'src/app/modelo/mascota';
import { MascotaService } from 'src/app/servicio/mascota.service';
import {HttpClient} from '@angular/common/http';
import { Router } from '@angular/router';

@Component({
  selector: 'app-mostrar-mascotas',
  templateUrl: './mostrar-mascotas.component.html',
  styleUrls: ['./mostrar-mascotas.component.css']
})
export class MostrarMascotasComponent {

  mascotaSeleccionadaMostrar!: Mascota;
  mascotaSeleccionadaModificar?: Mascota | null =  null;
  mascotas: Mascota[]  = [];
  showFormulario = false;

  constructor(private http: HttpClient, private service: MascotaService, private router: Router) { }

  ngOnInit() {
    this.service.findAll().subscribe(mascotas => {
      this.mascotas = mascotas;
    });
  }

  crearMascota() {
    this.showFormulario = true; 
  }
  formularioCerrado() {
    this.mascotaSeleccionadaModificar = null; 
    this.showFormulario = false;  
  }


  eliminarMascota(mascota: Mascota) {
    this.service.deleteById(mascota.id).subscribe(() => {
      this.service.findAll().subscribe(mascotas => {
        this.mascotas = mascotas;
      })
    })
  }

  mostrarMascota(mascota: Mascota) {
    this.router.navigate(['/mascota/mostrar-mascota', mascota.id]);
  }

  actualizarMascota(mascota: Mascota) {
    this.mascotaSeleccionadaModificar = mascota;
    this.showFormulario = true;  
  }

  toggleFormulario() {
    this.showFormulario = !this.showFormulario;
  }
}
<link href="https://unpkg.com/boxicons@2.1.4/css/boxicons.min.css" rel="stylesheet"/>
<body>
  <h1>Información de Mascotas</h1>
  <section id="btn-inicial" class="acciones">
    <a type="button" class="btn" id="boton-agregarMascota" [routerLink]="['/mascota/add']">
      <i class='bx bx-plus-circle'></i> Agregar Mascota
    </a>
  </section>
  <main>
    <table>
      <thead>
        <tr>
          <th>Foto</th>
          <th>Nombre</th>
          <th>Raza</th>
          <th>Edad</th>
          <th>Peso</th>
          <th>Enfermedad</th>
          <th>Estado Activo</th>
          <th></th>
          <th></th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let mascota of mascotas">
          <td>
            <img [src]="mascota.foto" [alt]="'Foto de ' + mascota.nombre">
          </td>
          <td>{{mascota.nombre}}</td>
          <td>{{mascota.raza}}</td>
          <td>{{mascota.edad}}</td>
          <td>{{mascota.peso}}</td>
          <td>{{mascota.enfermedad ? mascota.enfermedad : 'Vacio'}}</td>
          <td>{{mascota.estadoActivo ? 'Si' : 'No'}}</td>
          <td>
            <a [routerLink]="['/mascota/mostrar-mascota/',mascota.id]">
              <button class="btn">
                <i class='bx bxs-show'></i>
              </button>
            </a>
            <a [routerLink]="['/mascota/update/',mascota.id]">
              <button class="btn">
                <i class='bx bx-edit'></i>
              </button>
            </a>
            <button class="btn" (click)="eliminarMascota(mascota)">
              <i class='bx bx-trash'></i>
            </button>
            </td>
        </tr>
      </tbody>
    </table>
  </main>
</body>
