import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-mostrar-num-items',
  templateUrl: './mostrar-num-items.component.html',
  styleUrls: ['./mostrar-num-items.component.css']
})
export class MostrarNumItemsComponent {

  nombreItem:string | null = null;
  cantidad:string | null = null;

  constructor(
    private route: ActivatedRoute
  ) { }

  ngOnInit() {
    this.nombreItem = this.route.snapshot.paramMap.get('nombre-item');
    this.cantidad = this.route.snapshot.paramMap.get('cantidad');
  }
}
<div class="num-item-container">
  <button class="back-btn" onclick="window.history.back()">← Volver</button>

  <div class="card">
    <i class="bx bxs-bar-chart-alt-2 icono-kpi"></i>
    <h2>{{ nombreItem }}</h2>
    <p class="cantidad">{{ cantidad }}</p>
  </div>
</div>
