import { Component, Input, OnInit } from '@angular/core';
import { Cliente } from 'src/app/modelo/cliente';
import { HttpClient } from '@angular/common/http';
import { ActivatedRoute } from '@angular/router';
import { ClienteService } from 'src/app/servicio/cliente.service';
import { Mascota } from 'src/app/modelo/mascota';
import { MascotaService } from 'src/app/servicio/mascota.service';


@Component({
  selector: 'app-mostrar-cliente',
  templateUrl: './mostrar-cliente.component.html',
  styleUrls: ['./mostrar-cliente.component.css']
})
export class MostrarClienteComponent implements OnInit{
  cliente!: Cliente;
  mascotas!: Mascota[];

  constructor(private http: HttpClient,
    private route: ActivatedRoute,
    private servicioCliente: ClienteService,
    private servicioMascota: MascotaService) { }

  ngOnInit(): void {
    const idParam = this.route.snapshot.paramMap.get('id');
    if (idParam !== null) {
      const id = Number(idParam);
      this.servicioCliente.findById(id).subscribe(cliente => {
        this.cliente = cliente;
        this.servicioMascota.findMascotasByClienteCedula(this.cliente.cedula).
        subscribe(mascotas => this.mascotas = mascotas);
      });
    } else {
      console.error('ID no encontrado en la ruta');
    }
  }

}
<body>
<h1>Información Dueño</h1>
<button class="btn btn-volver" onclick="window.history.back()"><i class='bx bx-arrow-back'></i>Volver</button>
<main>
  <section class="informacion">
    <div class="campos">
      <ng-container *ngIf="!cliente"><h1>Este cliente no existe</h1></ng-container>
      <p>Nombre:</p>
      <p><span>{{cliente.nombre}}</span></p>
      <p>Correo:</p>
      <p><span>{{cliente.correo}}</span></p>
      <p>Cédula:</p>
      <p><span>{{cliente.cedula}}</span></p>
      <p>Celular:</p>
      <p><span>{{cliente.celular}}</span></p>
    </div>
    <p class="msg-no-tiene-mascotas" *ngIf="mascotas && mascotas.length === 0">
      No tiene mascotas registradas.
    </p>
    <div id="mascotas" *ngIf="mascotas">
      <h3>Mascotas Registras</h3>
      <ul class="mascotas">
        <li *ngFor="let mascota of mascotas">
          <img [src]="mascota.foto" [alt]="'Imagen de mascota ' + mascota.nombre" />
          <div id="datos-animal">
            <p>{{mascota.nombre}}</p>
            <p>{{mascota.raza}}</p>
          </div>
          <a id="boton-ver-mascota" [routerLink]="['/mascota/mostrar-mascota/',mascota.id]">Ver Mascota</a>
        </li>
      </ul>
    </div>
  </section>
</main>
</body>
