import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-mostrar-valor-dinero',
  templateUrl: './mostrar-valor-dinero.component.html',
  styleUrls: ['./mostrar-valor-dinero.component.css']
})
export class MostrarValorDineroComponent {
  txtMostrar:string | null = null;
  dinero:string | null = null;

  constructor(
    private route:ActivatedRoute
  ) { }

  ngOnInit() {
    this.txtMostrar = this.route.snapshot.paramMap.get('txt-msg');
    this.dinero = this.route.snapshot.paramMap.get('dinero');
  }
}
<div class="money-container">
  <button class="back-button" onclick="window.history.back()">
    <i class="bx bx-arrow-back"></i> Volver
  </button>

  <div class="money-card">
    <i class='bx bx-dollar-circle icon'></i>
    <h2>{{txtMostrar}}</h2>
    <p class="amount">${{dinero}}</p>
  </div>
</div>

<style>
  .money-container {
    display: flex;
    flex-direction: column;
    align-items: center;
    padding: 3rem;
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
  }

  .back-button {
    background-color: #236e4a; /* verde oscuro */
    color: white;
    border: none;
    padding: 0.6rem 1.2rem;
    border-radius: 8px;
    cursor: pointer;
    font-size: 1rem;
    display: flex;
    align-items: center;
    gap: 0.5rem;
    margin-bottom: 2rem;
    transition: background-color 0.3s ease;
  }

  .back-button:hover {
    background-color: #1d593c;
  }

  .money-card {
    background: white;
    border-radius: 16px;
    box-shadow: 0 8px 16px rgba(0,0,0,0.1);
    padding: 2rem 3rem;
    text-align: center;
    max-width: 400px;
    width: 100%;
  }

  .money-card .icon {
    font-size: 3rem;
    color: #4CAF50;
    margin-bottom: 1rem;
  }

  .money-card h2 {
    font-size: 1.8rem;
    margin-bottom: 0.5rem;
  }

  .money-card .amount {
    font-size: 2rem;
    font-weight: bold;
    color: #333;
  }
</style>
