import { Component, Input } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { TratamientoService } from 'src/app/servicio/tratamiento.service';

@Component({
  selector: 'app-mostrar-lista-items',
  templateUrl: './mostrar-lista-items.component.html',
  styleUrls: ['./mostrar-lista-items.component.css']
})
export class MostrarListaItemsComponent {

  lista:any[] = [];
  tipoObjeto:string | null = null;
  mensaje:string|null = null;

  constructor(
    private route:ActivatedRoute,
    private tratamientoServicio:TratamientoService
  ) { }

  ngOnInit() {
    this.tipoObjeto = this.route.snapshot.paramMap.get('tipo');
    this.mensaje = this.route.snapshot.paramMap.get('mensaje');
    this.obtenerLista();
  }

  obtenerLista() {
    switch(this.tipoObjeto) {

      case 'tratamiento': this.tratamientoServicio.obtenerTop3TratamientosMasUnidadVendida()
      .subscribe(l => {this.lista = l;})
      break;

      default:
        this.lista = [];
    }
  }
}
<h2>Lista de {{ tipoObjeto | titlecase }}</h2>

<div [ngSwitch]="tipoObjeto">

  <!-- Caso: Tratamientos -->
  <div *ngSwitchCase="'tratamiento'">
    <table *ngIf="lista.length > 0" class="table-auto w-full border-collapse border border-gray-300">
      <thead>
        <tr>
          <th class="border p-2">Nombre</th>
          <th class="border p-2">Fecha</th>
          <th class="border p-2">Veterinario</th>
          <th class="border p-2">Droga Asignada</th>
          <th class="border p-2">Mascota</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let t of lista">
          <td class="border p-2">{{t.nombreTratamiento}}</td>
          <td class="border p-2">{{t.fecha | date:"dd/MM/yyyy"}}</td>
          <td class="border p-2">{{t.drogaAsignada.nombre}}</td>
          <td class="border p-2">{{t.veterinario.nombre}}</td>
          <td class="border p-2">{{t.mascota.nombre}}</td>
        </tr>
      </tbody>
    </table>
    <p *ngIf="lista.length === 0">No hay tratamientos registrados.</p>
  </div>

  <!-- Otros casos: pendientes de agregar -->
  <div *ngSwitchDefault>
    <p>No hay una visualización definida para el tipo "{{ tipoObjeto }}".</p>
  </div>

</div>
