import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { ActivatedRoute, Router } from '@angular/router';
import { Tratamiento } from 'src/app/modelo/tratamiento';
import { TratamientoService } from 'src/app/servicio/tratamiento.service';

@Component({
  selector: 'app-tratamientos-veterinario',
  templateUrl: './tratamientos-veterinario.component.html',
  styleUrls: ['./tratamientos-veterinario.component.css']
})
export class TratamientosVeterinarioComponent {

  tratamientos: Tratamiento[] = [];
  terminoBusqueda: string = '';
  filtroSeleccionado: string = 'nombreTratamiento';
  veterinarioCedula!:string | null = null;

  constructor(private http: HttpClient, private service: TratamientoService, private route: ActivatedRoute, private router: Router) {}

  ngOnInit(): void {
    this.veterinarioCedula = this.route.snapshot.paramMap.get('cedula');
    if (this.veterinarioCedula !== null) {
      this.service.obtenerTratamientosVeterinario(this.veterinarioCedula).subscribe(tratamientos => {
        this.tratamientos = tratamientos;
      });
    } else {
      console.error('ID no encontrado en la ruta');
    }
  }

  get tratamientosFiltrados() {
    if (!this.terminoBusqueda) return this.tratamientos;

    const valor = this.terminoBusqueda.toLowerCase();

    return this.tratamientos.filter(tratamiento => {
      switch (this.filtroSeleccionado) {
        case 'nombreTratamiento':
          return tratamiento.nombreTratamiento.toLowerCase().includes(valor);
        case 'drogaAsignada':
          return tratamiento.drogaAsignada?.nombre?.toLowerCase().includes(valor);
        case 'mascota':
          return tratamiento.mascota?.nombre?.toLowerCase().includes(valor);

        default:
          return true;
      }
    });
  }
}
<link href="https://unpkg.com/boxicons@2.1.4/css/boxicons.min.css" rel="stylesheet"/>
<body>
  <h1>Información de Tratamientos</h1>

  <div id="seccion_busqueda_crear">
    <section id="btn-inicial" class="acciones">
      <a type="button" class="btn" id="boton-agregarMascota" [routerLink]="['/tratamiento/asignar-tratamiento',veterinarioCedula]">
        <i class='bx bx-plus-circle'></i> Agregar Tratamiento
      </a>
    </section>
    <section class="busqueda">
      <input type="text" placeholder="Buscar..." [(ngModel)]="terminoBusqueda" />
      <select [(ngModel)]="filtroSeleccionado">
        <option value="nombreTratamiento">Nombre Tratamiento</option>
        <option value="drogaAsignada">Droga</option>
        <option value="mascota">Mascota</option>
        <option value="veterinario">Veterinario</option>
      </select>
    </section>
  </div>

  <main>
    <table>
      <thead>
        <tr>
          <th>Foto Mascota</th>
          <th>Nombre Mascota</th>
          <th>Enfermedad</th>
          <th>Nombre Tratamiento</th>
          <th>Fecha</th>
          <th>Droga Asignada</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let tratamiento of tratamientosFiltrados">
          <td>
            <img [src]="tratamiento.mascota.foto" [alt]="'Foto de ' + tratamiento.mascota.nombre">
          </td>
          <td>{{ tratamiento.mascota.nombre }}</td>
          <td>{{ tratamiento.mascota.enfermedad === null ? "Vacio": tratamiento.mascota.enfermedad}}</td>
          <td>{{ tratamiento.nombreTratamiento }}</td>
          <td>{{ tratamiento.fecha | date:'yyyy-MM-dd' }}</td>
          <td>{{ tratamiento.drogaAsignada.nombre }}</td>

        </tr>
      </tbody>
    </table>
  </main>
</body>
