import { HttpClient } from '@angular/common/http';
import { Component, EventEmitter, Input, Output } from '@angular/core';
import { Router } from '@angular/router';
import { Cliente } from 'src/app/modelo/cliente';
import { ClienteService } from 'src/app/servicio/cliente.service';
import { Location } from '@angular/common'

@Component({
  selector: 'app-formulario-cliente',
  templateUrl: './formulario-cliente.component.html',
  styleUrls: ['./formulario-cliente.component.css']
})
export class FormularioClienteComponent {

  mostrarCedula!:boolean;

  @Input()
  cliente!: Cliente;
  @Output()
  eventoAgregarCliente = new EventEmitter<Cliente>();

  constructor(private servicioCliente: ClienteService,
    private http: HttpClient,
    private router: Router,
    private location:Location
  ) {}

  ngOnInit(): void {
    this.mostrarCedula = !this.cliente.cedula || this.cliente.cedula !== "";
  }
  submitFormulario() {
    if(this.cliente.id === -1) {
      this.servicioCliente.addCliente(this.cliente).subscribe({
        complete: () => this.location.back()
      });
    } else {
      this.servicioCliente.updateCliente(this.cliente).subscribe({
        complete: () => this.location.back()
      });
    }
  }
}
<form method="post">
  <label for="nombre">Nombre</label>
  <input [(ngModel)]="cliente.nombre" name="nombre" type="text" id="nombre" placeholder="Ingrese nombre" required>
  <label for="correo">Correo</label>
  <input [(ngModel)]="cliente.correo" name="correo" type="email" id="correo" placeholder="Ingrese su correro" required>
  <ng-container *ngIf="mostrarCedula">
    <label for="cedula">Cedula</label>
    <input [(ngModel)]="cliente.cedula" name="cedula" type="number" id="cedula" placeholder="Ingrese su cedula" required>
  </ng-container>
  <label for="celular">Celular</label>
  <input [(ngModel)]="cliente.celular" name="celular" type="number" id="celular" placeholder="Ingrese su celular" required>
  <button class="btn" type="submit" (click)="submitFormulario()">
    {{cliente.id === -1 ? "Agregar":"Actualizar"}}
    </button>
  <button onclick="window.history.back()">Volver</button>
</form>
