 <body>
    <h1>Datos de la Mascota</h1>
    <button class="btn btn-volver" (click)="volver()">
      <i class='bx bx-arrow-back'></i> Volver
    </button>
    <main>
      <ng-container *ngIf="!mascota"><h1>Esta mascota no existe</h1></ng-container>
      <div class="informacion-container" *ngIf="mascota">
        <img [src]="mascota.foto" [alt]="'Foto Mascota ' + mascota.nombre">
        <div class="campos">
          <p id="negrilla">Nombre:</p>
          <p>{{ mascota.nombre }}</p>
          <p id="negrilla">Raza:</p>
          <p>{{ mascota.raza }}</p>
          <p id="negrilla">Edad:</p>
          <p>{{ mascota.edad }}</p>
          <p id="negrilla">Peso:</p>
          <p>{{ mascota.peso }}</p>
          <p id="negrilla">Enfermedad:</p>
          <p>{{ mascota.enfermedad ? mascota.enfermedad : 'Vacio' }}</p>
          <p id="negrilla">Estado:</p>
          <p>{{ mascota.estadoActivo ? 'Activo' : 'No Activo' }}</p>
        </div>
      </div>
      <div class="seccion-tratamientos">
        <!-- Si no hay tratamientos -->
        <ng-container *ngIf="!tratamientos || tratamientos.length === 0">
          <p class="msg-no-tiene-tratamientos">{{ mascota.nombre }} no tiene tratamientos.</p>
        </ng-container>

        <!-- Si hay tratamientos -->
        <ol *ngIf="tratamientos && tratamientos.length > 0">
          <li *ngFor="let tratamiento of tratamientos">
            <p><strong>Nombre Tratamiento:</strong> {{ tratamiento.nombreTratamiento }}</p>
            <p><strong>Fecha:</strong> {{ tratamiento.fecha | date: "dd/MM/yyyy" }}</p>
            <p><strong>Droga Asignada:</strong> {{ tratamiento.drogaAsignada.nombre }}</p>
          </li>
        </ol>
      </div>
    </main>
  </body>
