import { HttpClient } from '@angular/common/http';
import { Component, OnInit, Output } from '@angular/core';
import { Cliente } from 'src/app/modelo/cliente';
import { Observable } from 'rxjs';
import { Router } from '@angular/router';
import { ClienteService } from 'src/app/servicio/cliente.service';


@Component({
  selector: 'app-mostrar-clientes',
  templateUrl: './mostrar-clientes.component.html',
  styleUrls: ['./mostrar-clientes.component.css']
})
export class MostrarClientesComponent implements OnInit {
  clientes: Cliente[] = [];
  constructor(private http: HttpClient, private router: Router, private service: ClienteService) {}


  ngOnInit(): void {
    this.service.findAll().subscribe(clientes => {
      this.clientes = clientes;
    });
  }


  mostrarCliente(cliente: Cliente) {
    this.router.navigate(['/cliente/mostrar-cliente', cliente.id]);
  }

  eliminarCliente(cliente: Cliente) {
    console.warn("Entro a eliminar cliente");
    this.service.deleteById(cliente.id).subscribe(() => {
    this.service.findAll().subscribe(clientes => {
        this.clientes = clientes;
      })
    });
  }

}  
<h1>Dueños de Mascotas</h1>
<section id="btn-inicial"class="acciones">
  <a type="button" class="btn" [routerLink]="['/cliente/add']"><i class='bx bx-plus-circle'></i> Agregar Dueño</a>
</section>
<main>
  <table>
    <thead>
      <tr>
        <th>Nombre</th>
        <th>Email</th>
        <th>Cédula</th>
        <th>Celular</th>
        <th></th>
        <th></th>
      </tr>
    </thead>
    <tbody>
      <p *ngIf="clientes.length === 0">No hay clientes para mostrar.</p>

      <tr *ngFor="let cliente of clientes">

        <td>{{cliente.nombre}}</td>
        <td>{{cliente.correo}}</td>
        <td>{{cliente.cedula}} </td>
        <td>{{cliente.celular}}</td>
        <td>
          <a>
            <button class="btn" (click)="mostrarCliente(cliente)"><i class='bx bxs-show'></i></button>
          </a>
          <a>
            <button class="btn" (click)="eliminarCliente(cliente)"><i class='bx bx-trash' ></i></button>
          </a>
          <a type="button" [routerLink]="['/cliente/update/',cliente.id]" class="btn">
            <i class='bx bx-edit'></i>
          </a>
        </td>
        <!-- <td>
          <a type="button" th:href="@{/cliente/mostrar-cliente/{id}(id=${cliente.id})}" class="btn">
            <i class='bx bxs-show'></i>
          </a>
          <a type="button" th:href="@{/cliente/update/{id}(id=${cliente.id})}" class="btn">
            <i class='bx bx-edit'></i>
          </a>
          <a type="button" th:href="@{/cliente/delete/{id}(id=${cliente.id})}" onclick="recargarDespues()" class="btn">
            <i class='bx bx-trash' ></i>
          </a>
        </td> -->
      </tr>
    </tbody>
  </table>
</main>
<footer>
</footer>
<script>
  function recargarDespues() {
      setTimeout(() => {
          window.location.reload();
      }, 300);
  }
</script>
